import React from 'react';
import { compose } from 'redux';
import { reduxForm, Field } from 'redux-form';
import Button from '@material-ui/core/Button';
import TextInput from '../../../common/TextField';

const SignInForm = ({handleSubmit, onCancel, pristine, submitting, valid}) => {
    
    const required = value => (value ? undefined : 'Required!');

    return (
        <form onSubmit={handleSubmit(val => console.log(val))}>
        <Field
            name="login"
            component={TextInput}
            label="Login"
            margin="dense"
            variant="outlined"
        />
        <Field
            name="password"
            component={TextInput}
            type="password"
            label="Password"
            margin="dense"
            variant="outlined"
        />
        <Button type="button" onClick={onCancel} variant="outlined" color="secondary">
          CANCEL
        </Button>
        <Button type="submit" disabled={!valid || pristine || submitting}>
          SUBMIT
        </Button>
      </form>
    );
}

export default reduxForm({
    form: 'a'
})(SignInForm);
