import { handleActions } from "redux-actions";

import * as actionCreators from "../actions";

const defaultState = [];
let keys = 1;

const reducer = handleActions(
  {
    [actionCreators.addNotification](state, { payload }) {
      keys++;
      console.log("ADD", keys);
      console.log(payload);
      return [...state, {key: keys, ...payload, options: {key: keys, ...payload.options}}];
    },
    [actionCreators.removeNotification](state, { payload }) {
      console.log("REMOVE", payload);
      return state.filter(n => n.key !== payload);
    },
    [actionCreators.removeAllNotifications](state, { payload }) {
        console.log("REMOVE_ALL", payload);
        return state.map(n =>
          !n || n.key === payload ? { ...n, dismissed: true } : { ...n }
        );
      },
  },
  defaultState
);

export default reducer;
