import { call, put, takeEvery} from 'redux-saga/effects';

import * as actionCreators from '../actions';
import * as modalActionCreators from '../../common/ModalDialog/actions';
import * as notificationActionCreators from '../../common/Notifications/actions';
import { USER, SIGN_IN_MODAL } from '../constants';

const request = ({login, password}) => {
    console.log(login, password)
    return new Promise((resolve, reject) => {
        return (login === 'bob', password === '123') ? setTimeout(() => resolve(USER), 1000) : reject(new Error('Неверные данные'))
    });
}

function* signIn(action) {
    try {
        const user = yield call(request, action.payload);
        console.log(user);
        yield signInSuccess(user);
    } catch (error) {
        console.log(error)
        yield put(actionCreators.signInFail);
    }
}

function* signInSuccess(user) {
    yield put(actionCreators.signInSuccess(user));
    yield put(notificationActionCreators.addNotification({
        message: 'OK',
        variant: 'success'
    }))
    yield put(modalActionCreators.toggleModal(SIGN_IN_MODAL.modalName));
    // TODO редирект
}

function* sendRequest() {
    yield takeEvery(actionCreators.signInRequest, signIn)
}

export default sendRequest;
