import React, { Component, Fragment } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import Button from '@material-ui/core/Button';

import SignInModal from '../components/SignInModal';
import * as modalActionCreators from '../../common/ModalDialog/actions';
import { SIGN_IN_MODAL } from '../constants';

class SignInContainer extends Component {

    openDialog = () => {
        this.props.modalActions.toggleModal(SIGN_IN_MODAL.modalName);
    };

    onSubmit = (formValues) => {
        console.log(formValues)
    }

    onCancel = () => {
        this.props.modalActions.toggleModal(SIGN_IN_MODAL.modalName);
    }

    render() {
        const modalProps = SIGN_IN_MODAL;

        const formProps = {
            form: 'SIGN_IN_FORM',
            onCancel: onCancel,
            onSubmit: onSubmit
        };
    
        return (
            <Fragment>
                <Button variant="outlined" color="primary" onClick={this.openDialog}>
                    Войти
                </Button>
                <SignInModal modal={modalProps} form={formProps} />
            </Fragment>
        );
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        modalActions: bindActionCreators(modalActionCreators, dispatch),
    };
};

export default connect(null, mapDispatchToProps)(SignInContainer);
