import React from 'react';
import TextField from '@material-ui/core/TextField'

const TextInput = ({
    input,
    label,
    meta: { touched, error },
    ...custom
  }) =>
    <TextField
        hintText={label}
        errorText={touched && error}
        {...input}
        {...custom}
    />

export default TextInput;
