import React, { Component, Fragment } from 'react';
import { bindActionCreators } from 'redux';
import Button from '@material-ui/core/Button';

import SignInModal from '..SignInModal';
import * as modalActionCreators from 'modules/common/ModalDialog/actions';

import { store } from '../../../components/App';

class SignInContainer extends Component {

    openDialog = () => {
        store.dispatch(actionCreators.openModal());
    };

    render() {
        return (
            <Fragment>
                <Button variant="outlined" color="primary" onClick={this.openDialog}>
                    Войти
                </Button>
                <SignInModal />
            </Fragment>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        isPending: apiSelectors.createPendingSelector(actionsCreators.addCommentRequest)(state),
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        modalActions: bindActionCreators(modalActionCreators, dispatch),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(SignInContainer);
