import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { withSnackbar } from "notistack";

import * as actionCreators from "../actions";
import Notification from "../components";
import { getNotifications } from "../selectors";

class NotificationContainer extends Component {
  /*   remove = id => {
    this.props.actions.removeNotification(id);
  };

  render() {
    return this.props.notifications.map(n => (
      <Notification key={n.id} {...n} onClose={this.remove} />
    ));
  } */

  state = {
    displayed: []
  };

  storeDisplayed = key => {
    this.setState(({ displayed }) => ({
      displayed: [...displayed, key]
    }));
  };

  render() {
    const { notifications, enqueueSnackbar } = this.props;
    const { removeSnackbar } = this.props.actions;
    const { displayed } = this.state;

    notifications.forEach(notification => {
      setTimeout(() => {
        // If notification already displayed, abort
        if (displayed.indexOf(notification.key) > -1) return;
        // Display notification using notistack
        enqueueSnackbar(notification.message, notification.options);
        // Add notification's key to the local state
        this.storeDisplayed(notification.key);
        // Dispatch action to remove the notification from the redux store
        removeSnackbar(notification.key);
      }, 1);
    });

    return null;
  }
}

const mapStateToProps = state => {
  return {
    notifications: getNotifications(state)
  };
};

const mapDispatchToProps = dispatch => {
  return {
    actions: bindActionCreators(actionCreators, dispatch)
  };
};

export default withSnackbar(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(NotificationContainer)
);
