import React from "react";
import { ConnectedRouter } from "connected-react-router";
import { Switch, Route } from "react-router";
import { Provider } from "react-redux";

import "bootstrap/dist/css/bootstrap.min.css";

import { configureStore, history } from "../../store/configureStore";
import Header from "../../modules/common/Header";
import FilmsList from "../../modules/films";
import Notifications from "../../modules/common/Notifications";
import UserAccount from "../../modules/userAccount";
import PrivateRoute from "../../modules/common/PrivateRoute";
import { SnackbarProvider } from "notistack";

export const store = configureStore();

const App = () => {
  const SnackbarProviderOptions = {
    maxSnack: 3,
    anchorOrigin: {
      vertical: "top",
      horizontal: "right"
    }
  };

  return (
    <Provider store={store}>
      <ConnectedRouter history={history}>
        <SnackbarProvider {...SnackbarProviderOptions}>
          <div className="container">
            <Notifications />

            <Header />

            <Switch>
              <Route path="/" component={FilmsList} />
              <Route path="/films" component={FilmsList} />
{/*               <Route path="/categories" component={} />
              <Route path="/ratings" component={} /> */}
              <PrivateRoute path="/account" component={UserAccount} />
            </Switch>
          </div>
        </SnackbarProvider>
      </ConnectedRouter>
    </Provider>
  );
};

export default App;
