import React from 'react';
import { compose } from 'redux';
import { reduxForm, Field } from 'redux-form';
import TextInput from '../../../common/TextField';

const SignInForm = ({handleSubmit, onCancel, pristine, submitting, valid}) => {
    
    const required = value => (value ? null : 'Required!');

    return (
        <form onSubmit={handleSubmit(val => console.log(val))}>
        <Field
            name="login"
            component={TextInput}
            label="Login"
            margin="dense"
            variant="outlined"
            validate={[required]}
        />
        <Field
            name="password"
            component={TextInput}
            type="password"
            label="Password"
            margin="dense"
            variant="outlined"
            validate={[required]}
        />
        <button type="button" onClick={onCancel} color="primary">
          CANCEL
        </button>
        <button type="submit" disabled={!valid || pristine || submitting}>
          SUBMIT
        </button>
      </form>
    );
}

export default reduxForm({
    form: 'a'
})(SignInForm);
