import React from 'react';
import TextField from '@material-ui/core/TextField'

const TextInput = ({
    input,
    label,
    placeholder,
    meta: { touched, error },
    ...others
  }) =>
/*     <TextField
        label={label}
        placeholder={placeholder}
        helperText={touched && error}
        {...input}
        {...others}
        fullWidth
    /> */
    <FormControl>
    <TextField
        label={label}
      {...input}
      error={Boolean(error)}
      {...others}
    />
    {error && (
      <span>
        <ErrorOutline />
        {error}
      </span>
    )}
  </FormControl>

export default TextInput;
