import React from 'react';
import { compose } from 'redux';
import { reduxForm, Field } from 'redux-form';
import Button from '@material-ui/core/Button';
import TextInput from '../../../common/TextField';

import { styles } from './styles';

const SignInForm = ({handleSubmit, onCancel, pristine, submitting, valid}) => {
    
    const required = value => (value ? undefined : 'Required!');

    return (
        <form onSubmit={handleSubmit}>
        <Field
            name="login"
            component={TextInput}
            label="Login"
            margin="dense"
            variant="outlined"
            validate={[required]}
        />
        <Field
            name="password"
            component={TextInput}
            type="password"
            label="Password"
            margin="dense"
            variant="outlined"
        />

        <div style={styles.buttonWrapper}>
            <Button type="button" onClick={onCancel} variant="outlined" color="secondary">
                CANCEL
            </Button>
            <Button style={styles.submitButton} type="submit" disabled={!valid || pristine || submitting} variant="contained" color="primary">
                SUBMIT
            </Button>
        </div>
      </form>
    );
}

export default reduxForm({
    form: 'a'
})(SignInForm);
