import React from 'react';
import { compose } from 'redux';
import { reduxForm, Field } from 'redux-form';
import Button from '@material-ui/core/Button';
import TextInput from '../../../common/TextField';
import RefreshIndicator from 'material-ui/RefreshIndicator';

import { styles } from './styles';

export const required = value => (value ? undefined : 'Обязательное поле!');

const SignInForm = ({handleSubmit, onCancel, valid, isPending}) => {

    return (
        <form onSubmit={handleSubmit}>
            <Field
                name="login"
                component={TextInput}
                label="Логин"
                margin="dense"
                variant="outlined"
                validate={[required]}
            />
            <Field
                name="password"
                component={TextInput}
                type="password"
                label="Пароль"
                margin="dense"
                variant="outlined"
                validate={[required]}
            />

            <div style={styles.buttonWrapper}>
                <Button disabled={isPending} type="button" onClick={onCancel} variant="outlined" color="secondary">
                    ЗАКРЫТЬ
                </Button>
                <RefreshIndicator
      size={50}
      top={0}
      left={0}
      loadingColor="#FF9800"
      status="loading"
      style={{
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%,-50%)'
      }}
    />
{/*                 <Button style={styles.submitButton} type="submit" disabled={!valid} variant="contained" color="primary">
                    ВОЙТИ
                </Button> */}
            </div>
        </form>
    );
}

export default compose(
    reduxForm()
)(SignInForm);
