import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import Button from '@material-ui/core/Button';

import * as actionCreators from '../../ModalDialog/actions';
import SignInModal from '../../../auth/components/SignInModal';

import { store } from '../../../../components/App';

class Header extends Component {

    openDialog = () => {
        store.dispatch(actionCreators.openModal());
    };

    render() {
        return (
            <nav className="navbar navbar-dark bg-dark navbar-expand-lg">
                <Link to="/films" className="navbar-brand">Kinopoisk</Link>
                <div className="collapse navbar-collapse">
                    <ul className="navbar-nav mr-auto">
                        <li className="navbar-item">
                            <Link to="/films" className="nav-link">Фильмы</Link>
                        </li>
                        <li className="navbar-item">
                            <Link to="/categories" className="nav-link">Категории</Link>
                        </li>
                        <li className="navbar-item">
                            <Link to="/ratings" className="nav-link">Рейтинги</Link>
                        </li>
                    </ul>
                </div>
                <Button variant="outlined" color="primary" onClick={this.openDialog}>
                    Войти
                </Button>
                <SignInModal />
            </nav>
        );
    }
}

export default Header;
